mapper.insert:


INSERT INTO reservation
	(reservNum, reNum, userId, reservAir, reservPerseon, seatLevel, seatNum)
	SELECT #{reservNum}, #{reNum}, #{userId}, #{reservAir}, #{reservPerseon}, #{seatLevel}, #{seatNum}
	FROM DUAL
	WHERE NOT EXISTS (
    SELECT 
		*
    FROM 
		reservation
    WHERE 
		reNum = #{reNum}
        AND userId = #{userId}
)LIMIT 1

mapper.select:
select r.reservNum, r.reservAir, r.reservPerseon, r.seatLevel, r.seatNum, u.userId, u.userName, a.reNum, a.startCountry, a.startCity, a.finishCountry, a.finishCity, a.startDate, a.startTime, a.finishDate, a.finishTime
from reservation r inner join user u on(r.userId = u.userId)
inner join airport a on (r.reNum = a.reNum) where u.userId=#{userId} order by r.reservNum DESC

select r.reservNum, r.reservAir, r.reservPerseon, r.seatLevel, r.seatNum, u.userId, u.userName, a.reNum, a.startCountry, a.startCity, a.finishCountry, a.finishCity, a.startDate, a.startTime, a.finishDate, a.finishTime
from reservation r inner join user u on(r.userId = u.userId)
inner join airport a on (r.reNum = a.reNum) where u.userId=#{userId} order by r.reservNum DESC
